fn from(P: &'a RistrettoPoint) -> BatchCompressState {
    let XX = P.0.X.square();
    let YY = P.0.Y.square();
    let ZZ = P.0.Z.square();
    let dTT = &P.0.T.square() * &constants::EDWARDS_D;

    let e = &P.0.X * &(&P.0.Y + &P.0.Y); // = 2*X*Y
    let f = &ZZ + &dTT;                  // = Z^2 + d*T^2
    let g = &YY + &XX;                   // = Y^2 - a*X^2
    let h = &ZZ - &dTT;                  // = Z^2 - d*T^2

    let eg = &e * &g;
    let fh = &f * &h;

    BatchCompressState{ e, f, g, h, eg, fh }
}

fn default() -> AffineNielsPoint {
    AffineNielsPoint::identity()
}

pub fn double(&self) -> CompletedPoint {
    // Double()
    let XX = self.X.square();
    let YY = self.Y.square();
    let ZZ2 = self.Z.square2();
    let X_plus_Y = &self.X + &self.Y;
    let X_plus_Y_sq = X_plus_Y.square();
    let YY_plus_XX = &YY + &XX;
    let YY_minus_XX = &YY - &XX;

    CompletedPoint {
        X: &X_plus_Y_sq - &YY_plus_XX,
        Y: YY_plus_XX,
        Z: YY_minus_XX,
        T: &ZZ2 - &YY_minus_XX,
    }
}

pub fn is_small_order(&self) -> bool {
    self.mul_by_cofactor().is_identity()
}

pub proof fn shl_decomposition(v: u64, a: nat, b: nat)
    requires
        (a + b) < 64,
        v * pow2(a + b) <= u64::MAX
    ensures
        (v << (a + b)) == ((v << a) << b)
{
    if (a == 0 || b == 0) {
        broadcast use shl_zero_is_id;
    }
    else {
        // 2^(a + b) == 2^a * 2^b
        lemma_pow2_adds(a, b);
        // 2^a < 2^(a + b) ...
        lemma_pow2_strictly_increases(a, a + b);
        // ..., which implies v * 2^a < v * 2^(a + b) <= u64::MAX
        mul_le(v as nat, v as nat, pow2(a), pow2(a + b));
        // v << a + b = v * 2^(a+b)
        lemma_u64_shl_is_mul(v, (a + b) as u64);
        // v << a = v * 2^a
        lemma_u64_shl_is_mul(v, a as u64);
        // (v * 2^a) * 2^b = v * (2^a * 2^b)
        lemma_mul_is_associative(v as int, pow2(a) as int, pow2(b) as int);
        // (v * 2^a) << b = (v * 2^a) * 2^b
        lemma_u64_shl_is_mul((v * pow2(a)) as u64, b as u64);
    }
}

