}

fn from(A: &'a EdwardsPoint) -> Self {
    let mut Ai = [A.as_projective_niels(); 8];
    let A2 = A.double();
    for i in 0..7 {
        Ai[i + 1] = (&A2 + &Ai[i]).as_extended().as_projective_niels();
    }
    // Now Ai = [A, 3A, 5A, 7A, 9A, 11A, 13A, 15A]
    NafLookupTable5(Ai)
}

fn conditional_select(
    a: &FieldElement51,
    b: &FieldElement51,
    choice: Choice,
) -> FieldElement51 {
    FieldElement51([
        u64::conditional_select(&a.0[0], &b.0[0], choice),
        u64::conditional_select(&a.0[1], &b.0[1], choice),
        u64::conditional_select(&a.0[2], &b.0[2], choice),
        u64::conditional_select(&a.0[3], &b.0[3], choice),
        u64::conditional_select(&a.0[4], &b.0[4], choice),
    ])
}

fn part1(sum: u128) -> (res: (u128, u64))
{
    assume(false); // TODO: Add proper bounds checking and proofs
    let p = (sum as u64).wrapping_mul(constants::LFACTOR) & ((1u64 << 52) - 1);
    let carry = (sum + m(p, constants::L.limbs[0])) >> 52;
    (carry, p)
}

pub fn from_montgomery(&self) -> (result: Scalar52)
requires
    limbs_bounded(self),
ensures
    limbs_bounded(&result),
    (to_nat(&result.limbs) * montgomery_radix()) % group_order() == to_nat(&self.limbs) % group_order(),
{
    let mut limbs = [0u128; 9];
    #[allow(clippy::needless_range_loop)]
    for i in 0..5
        invariant
            forall|j: int| #![auto] 0 <= j < i ==> limbs[j] == self.limbs[j] as u128,
            forall|j: int| #![auto] i <= j < 9 ==> limbs[j] == 0,
    {
        limbs[i] = self.limbs[i] as u128;
    }
    let result = Scalar52::montgomery_reduce(&limbs);
    proof {
        lemma_from_montgomery_limbs_conversion(&limbs, &self.limbs);
    }
    result
}

pub proof fn lemma_pow2_square(v: int, i: nat)
    ensures
        pow(v, pow2(i)) * pow(v, pow2(i)) == pow(v, pow2(i + 1))
{
    // pow(v, pow2(i)) * pow(v, pow2(i)) = pow(v, pow2(i) + pow2(i));
    lemma_pow_adds(v as int, pow2(i), pow2(i));
    // 2 * pow2(i) = pow2(i + 1)
    lemma_pow2_unfold(i + 1);
}

pub(crate) fn non_adjacent_form(&self, w: usize) -> [i8; 256] {
    // required by the NAF definition
    debug_assert!(w >= 2);
    // required so that the NAF digits fit in i8
    debug_assert!(w <= 8);

    let mut naf = [0i8; 256];

    let mut x_u64 = [0u64; 5];
    read_le_u64_into(&self.bytes, &mut x_u64[0..4]);

    let width = 1 << w;
    let window_mask = width - 1;

    let mut pos = 0;
    let mut carry = 0;
    while pos < 256 {
        // Construct a buffer of bits of the scalar, starting at bit `pos`
        let u64_idx = pos / 64;
        let bit_idx = pos % 64;
        let bit_buf: u64 = if bit_idx < 64 - w {
            // This window's bits are contained in a single u64
            x_u64[u64_idx] >> bit_idx
        } else {
            // Combine the current u64's bits with the bits from the next u64
            (x_u64[u64_idx] >> bit_idx) | (x_u64[1 + u64_idx] << (64 - bit_idx))
        };

        // Add the carry into the current window
        let window = carry + (bit_buf & window_mask);

        if window & 1 == 0 {
            // If the window value is even, preserve the carry and continue.
            // Why is the carry preserved?
            // If carry == 0 and window & 1 == 0, then the next carry should be 0
            // If carry == 1 and window & 1 == 0, then bit_buf & 1 == 1 so the next carry should be 1
            pos += 1;
            continue;
        }

        if window < width / 2 {
            carry = 0;
            naf[pos] = window as i8;
        } else {
            carry = 1;
            naf[pos] = (window as i8).wrapping_sub(width as i8);
        }

        pos += w;
    }

    naf
}

