fn ct_eq(&self, other: &RistrettoPoint) -> Choice {
    let X1Y2 = &self.0.X * &other.0.Y;
    let Y1X2 = &self.0.Y * &other.0.X;
    let X1X2 = &self.0.X * &other.0.X;
    let Y1Y2 = &self.0.Y * &other.0.Y;

    X1Y2.ct_eq(&Y1X2) | X1X2.ct_eq(&Y1Y2)
}

