pub(super) fn step_2(s: FieldElement) -> (Choice, Choice, Choice, RistrettoPoint) {
    // Step 2.  Compute (X:Y:Z:T).
    let one = FieldElement::ONE;
    let ss = s.square();
    let u1 = &one - &ss; //  1 + as
    let u2 = &one + &ss; //  1 - as    where a=-1
    let u2_sqr = u2.square(); // (1 - as)

    // v == ad(1+as) - (1-as)            where d=-121665/121666
    let v = &(&(-&constants::EDWARDS_D) * &u1.square()) - &u2_sqr;

    let (ok, I) = (&v * &u2_sqr).invsqrt(); // 1/sqrt(v*u_2)

    let Dx = &I * &u2; // 1/sqrt(v)
    let Dy = &I * &(&Dx * &v); // 1/u2

    // x == | 2s/sqrt(v) | == + sqrt(4s/(ad(1+as) - (1-as)))
    let mut x = &(&s + &s) * &Dx;
    let x_neg = x.is_negative();
    x.conditional_negate(x_neg);

    // y == (1-as)/(1+as)
    let y = &u1 * &Dy;

    // t == ((1+as) sqrt(4s/(ad(1+as) - (1-as))))/(1-as)
    let t = &x * &y;

    (
        ok,
        t.is_negative(),
        y.is_zero(),
        RistrettoPoint(EdwardsPoint {
            X: x,
            Y: y,
            Z: one,
            T: t,
        }),
    )
}

fn optional_mixed_multiscalar_mul<I, J, K>(
    &self,
    static_scalars: I,
    dynamic_scalars: J,
    dynamic_points: K,
) -> Option<Self::Point>
where
    I: IntoIterator,
    I::Item: Borrow<Scalar>,
    J: IntoIterator,
    J::Item: Borrow<Scalar>,
    K: IntoIterator<Item = Option<Self::Point>>,
{
    let static_nafs = static_scalars
        .into_iter()
        .map(|c| c.borrow().non_adjacent_form(5))
        .collect::<Vec<_>>();
    let dynamic_nafs: Vec<_> = dynamic_scalars
        .into_iter()
        .map(|c| c.borrow().non_adjacent_form(5))
        .collect::<Vec<_>>();

    let dynamic_lookup_tables = dynamic_points
        .into_iter()
        .map(|P_opt| P_opt.map(|P| NafLookupTable5::<ProjectiveNielsPoint>::from(&P)))
        .collect::<Option<Vec<_>>>()?;

    let sp = self.static_lookup_tables.len();
    let dp = dynamic_lookup_tables.len();
    assert!(sp >= static_nafs.len());
    assert_eq!(dp, dynamic_nafs.len());

    // We could save some doublings by looking for the highest
    // nonzero NAF coefficient, but since we might have a lot of
    // them to search, it's not clear it's worthwhile to check.
    let mut S = ProjectivePoint::identity();
    for j in (0..256).rev() {
        let mut R: CompletedPoint = S.double();

        for i in 0..dp {
            let t_ij = dynamic_nafs[i][j];
            match t_ij.cmp(&0) {
                Ordering::Greater => {
                    R = &R.as_extended() + &dynamic_lookup_tables[i].select(t_ij as usize)
                }
                Ordering::Less => {
                    R = &R.as_extended() - &dynamic_lookup_tables[i].select(-t_ij as usize)
                }
                Ordering::Equal => {}
            }
        }

        #[allow(clippy::needless_range_loop)]
        for i in 0..static_nafs.len() {
            let t_ij = static_nafs[i][j];
            match t_ij.cmp(&0) {
                Ordering::Greater => {
                    R = &R.as_extended() + &self.static_lookup_tables[i].select(t_ij as usize)
                }
                Ordering::Less => {
                    R = &R.as_extended() - &self.static_lookup_tables[i].select(-t_ij as usize)
                }
                Ordering::Equal => {}
            }
        }

        S = R.as_projective();
    }

    Some(S.as_extended())
}

pub fn to_montgomery(&self) -> MontgomeryPoint {
    // We have u = (1+y)/(1-y) = (Z+Y)/(Z-Y).
    //
    // The denominator is zero only when y=1, the identity point of
    // the Edwards curve.  Since 0.invert() = 0, in this case we
    // compute the 2-torsion point (0,0).
    let U = &self.Z + &self.Y;
    let W = &self.Z - &self.Y;
    let u = &U * &W.invert();
    MontgomeryPoint(u.to_bytes())
}

pub fn compress_batch(inputs: &[EdwardsPoint]) -> Vec<CompressedEdwardsY> {
    let mut zs = inputs.iter().map(|input| input.Z).collect::<Vec<_>>();
    FieldElement::batch_invert(&mut zs);

    inputs
        .iter()
        .zip(&zs)
        .map(|(input, recip)| {
            let x = &input.X * recip;
            let y = &input.Y * recip;
            AffinePoint { x, y }.compress()
        })
        .collect()
}

