pub fn to_bytes(self) -> (r: [u8; 32])
    ensures
        true // No overflow
        // TODO:
        // as_nat(self.limbs) =?= as_nat_32_u8(r),
        // canonical encoding
        // forall|i: int| 0 <= i < 5 ==> r[i] < (1u64 << 51)
{
    proof {
        let l = spec_reduce(self.limbs);
        lemma_reduce(self.limbs);

        let q0 = (l[0] + 19) as u64 >> 51;
        let q1 = (l[1] + q0) as u64 >> 51;
        let q2 = (l[2] + q1) as u64 >> 51;
        let q3 = (l[3] + q2) as u64 >> 51;
        let q4 = (l[4] + q3) as u64 >> 51;

        assert(19 < (1u64 << 52)) by (bit_vector);
        lemma_add_then_shift(l[0], 19);
        lemma_add_then_shift(l[1], q0);
        lemma_add_then_shift(l[2], q1);
        lemma_add_then_shift(l[3], q2);
        lemma_add_then_shift(l[4], q3);

        let l0 = (l[0] + 19 * q4) as u64;
        let l1 = (l[1] + (l0 >> 51)) as u64;
        let l2 = (l[2] + (l1 >> 51)) as u64;
        let l3 = (l[3] + (l2 >> 51)) as u64;
        let l4 = (l[3] + (l3 >> 51)) as u64;

        assert( 19 * q4 < 1u64 << 7) by {
            // Explicit values for pow2(k) for k < 64
            lemma2_to64();
            shift_is_pow2(5); // now we know 19 < 1u64 << 5 for free
            shift_is_pow2(2);
            shift_is_pow2(7);
            lemma_pow2_adds(5, 2);
        }
        assert(((1u64 << 7)) + (1u64 << 52) < (1u64 << 53)) by (bit_vector);
        assert(((1u64 << 13)) + (1u64 << 52) < (1u64 << 53)) by (bit_vector);
        shifted_lt(l0, 51);
        shifted_lt(l1, 51);
        shifted_lt(l2, 51);
        shifted_lt(l3, 51);

        l51_bit_mask_lt();

        // TODO
        // let rr = [
        //     l0 & LOW_51_BIT_MASK,
        //     l1 & LOW_51_BIT_MASK,
        //     l2 & LOW_51_BIT_MASK,
        //     l3 & LOW_51_BIT_MASK,
        //     l4 & LOW_51_BIT_MASK
        // ];

        // let r = [
        //     rr[0]                           as u8,
        //     (rr[0] >>  8)                    as u8,
        //     (rr[0] >> 16)                    as u8,
        //     (rr[0] >> 24)                    as u8,
        //     (rr[0] >> 32)                    as u8,
        //     (rr[0] >> 40)                    as u8,
        //     ((rr[0] >> 48) | (rr[1] << 3)) as u8,
        //     (rr[1] >>  5)                    as u8,
        //     (rr[1] >> 13)                    as u8,
        //     (rr[1] >> 21)                    as u8,
        //     (rr[1] >> 29)                    as u8,
        //     (rr[1] >> 37)                    as u8,
        //     ((rr[1] >> 45) | (rr[2] << 6)) as u8,
        //     (rr[2] >>  2)                    as u8,
        //     (rr[2] >> 10)                    as u8,
        //     (rr[2] >> 18)                    as u8,
        //     (rr[2] >> 26)                    as u8,
        //     (rr[2] >> 34)                    as u8,
        //     (rr[2] >> 42)                    as u8,
        //     ((rr[2] >> 50) | (rr[3] << 1)) as u8,
        //     (rr[3] >>  7)                    as u8,
        //     (rr[3] >> 15)                    as u8,
        //     (rr[3] >> 23)                    as u8,
        //     (rr[3] >> 31)                    as u8,
        //     (rr[3] >> 39)                    as u8,
        //     ((rr[3] >> 47) | (rr[4] << 4)) as u8,
        //     (rr[4] >>  4)                    as u8,
        //     (rr[4] >> 12)                    as u8,
        //     (rr[4] >> 20)                    as u8,
        //     (rr[4] >> 28)                    as u8,
        //     (rr[4] >> 36)                    as u8,
        //     (rr[4] >> 44)                    as u8
        // ];

    }
    // Let h = limbs[0] + limbs[1]*2^51 + ... + limbs[4]*2^204.
    //
    // Write h = pq + r with 0 <= r < p.
    //
    // We want to compute r = h mod p.
    //
    // If h < 2*p = 2^256 - 38,
    // then q = 0 or 1,
    //
    // with q = 0 when h < p
    //  and q = 1 when h >= p.
    //
    // Notice that h >= p <==> h + 19 >= p + 19 <==> h + 19 >= 2^255.
    // Therefore q can be computed as the carry bit of h + 19.

    // First, reduce the limbs to ensure h < 2*p.
    let mut limbs = FieldElement51::reduce(self.limbs).limbs;

    let mut q = (limbs[0] + 19) >> 51;
    q = (limbs[1] + q) >> 51;
    q = (limbs[2] + q) >> 51;
    q = (limbs[3] + q) >> 51;
    q = (limbs[4] + q) >> 51;

    // Now we can compute r as r = h - pq = r - (2^255-19)q = r + 19q - 2^255q

    limbs[0] += 19 * q;

    // Now carry the result to compute r + 19q ...
    let low_51_bit_mask = (1u64 << 51) - 1;
    limbs[1] += limbs[0] >> 51;
    limbs[0] &= low_51_bit_mask;
    limbs[2] += limbs[1] >> 51;
    limbs[1] &= low_51_bit_mask;
    limbs[3] += limbs[2] >> 51;
    limbs[2] &= low_51_bit_mask;
    limbs[4] += limbs[3] >> 51;
    limbs[3] &= low_51_bit_mask;
    // ... but instead of carrying (limbs[4] >> 51) = 2^255q
    // into another limb, discard it, subtracting the value
    limbs[4] &= low_51_bit_mask;

    // Now arrange the bits of the limbs.
    let mut s = [0u8;32];
    s[ 0] =   limbs[0]                           as u8;
    s[ 1] =  (limbs[0] >>  8)                    as u8;
    s[ 2] =  (limbs[0] >> 16)                    as u8;
    s[ 3] =  (limbs[0] >> 24)                    as u8;
    s[ 4] =  (limbs[0] >> 32)                    as u8;
    s[ 5] =  (limbs[0] >> 40)                    as u8;
    s[ 6] = ((limbs[0] >> 48) | (limbs[1] << 3)) as u8;
    s[ 7] =  (limbs[1] >>  5)                    as u8;
    s[ 8] =  (limbs[1] >> 13)                    as u8;
    s[ 9] =  (limbs[1] >> 21)                    as u8;
    s[10] =  (limbs[1] >> 29)                    as u8;
    s[11] =  (limbs[1] >> 37)                    as u8;
    s[12] = ((limbs[1] >> 45) | (limbs[2] << 6)) as u8;
    s[13] =  (limbs[2] >>  2)                    as u8;
    s[14] =  (limbs[2] >> 10)                    as u8;
    s[15] =  (limbs[2] >> 18)                    as u8;
    s[16] =  (limbs[2] >> 26)                    as u8;
    s[17] =  (limbs[2] >> 34)                    as u8;
    s[18] =  (limbs[2] >> 42)                    as u8;
    s[19] = ((limbs[2] >> 50) | (limbs[3] << 1)) as u8;
    s[20] =  (limbs[3] >>  7)                    as u8;
    s[21] =  (limbs[3] >> 15)                    as u8;
    s[22] =  (limbs[3] >> 23)                    as u8;
    s[23] =  (limbs[3] >> 31)                    as u8;
    s[24] =  (limbs[3] >> 39)                    as u8;
    s[25] = ((limbs[3] >> 47) | (limbs[4] << 4)) as u8;
    s[26] =  (limbs[4] >>  4)                    as u8;
    s[27] =  (limbs[4] >> 12)                    as u8;
    s[28] =  (limbs[4] >> 20)                    as u8;
    s[29] =  (limbs[4] >> 28)                    as u8;
    s[30] =  (limbs[4] >> 36)                    as u8;
    s[31] =  (limbs[4] >> 44)                    as u8;

    // High bit should be zero.
    // DISABLED DUE TO NO VERUS SUPPORT FOR PANICS
    // debug_assert!((s[31] & 0b1000_0000u8) == 0u8);

    s
}

fn ct_eq(&self, other: &MontgomeryPoint) -> Choice {
    let self_fe = FieldElement::from_bytes(&self.0);
    let other_fe = FieldElement::from_bytes(&other.0);

    self_fe.ct_eq(&other_fe)
}

fn reduce(mut limbs: [u64; 5]) -> FieldElement51 {
    const LOW_51_BIT_MASK: u64 = (1u64 << 51) - 1;

    // Since the input limbs are bounded by 2^64, the biggest
    // carry-out is bounded by 2^13.
    //
    // The biggest carry-in is c4 * 19, resulting in
    //
    // 2^51 + 19*2^13 < 2^51.0000000001
    //
    // Because we don't need to canonicalize, only to reduce the
    // limb sizes, it's OK to do a "weak reduction", where we
    // compute the carry-outs in parallel.

    let c0 = limbs[0] >> 51;
    let c1 = limbs[1] >> 51;
    let c2 = limbs[2] >> 51;
    let c3 = limbs[3] >> 51;
    let c4 = limbs[4] >> 51;

    limbs[0] &= LOW_51_BIT_MASK;
    limbs[1] &= LOW_51_BIT_MASK;
    limbs[2] &= LOW_51_BIT_MASK;
    limbs[3] &= LOW_51_BIT_MASK;
    limbs[4] &= LOW_51_BIT_MASK;

    limbs[0] += c4 * 19;
    limbs[1] += c0;
    limbs[2] += c1;
    limbs[3] += c2;
    limbs[4] += c3;

    FieldElement51(limbs)
}

pub const fn as_bytes(&self) -> &[u8; 32] {
    &self.0
}

fn conditional_assign(&mut self, other: &Self, choice: Choice) {
    self.Y_plus_X.conditional_assign(&other.Y_plus_X, choice);
    self.Y_minus_X.conditional_assign(&other.Y_minus_X, choice);
    self.Z.conditional_assign(&other.Z, choice);
    self.T2d.conditional_assign(&other.T2d, choice);
}

pub proof fn lemma_cast_then_mod_51(x: u128)
    ensures
        (x as u64) % (pow2(51) as u64) == x % (pow2(51) as u128)
{
    lemma2_to64_rest(); // pow2(51 | 64)
    assert( (x as u64) % 0x8000000000000 == x % 0x8000000000000) by (bit_vector);
}

pub proof fn lemma_as_nat_sub(a: [u64;5], b: [u64;5])
    requires
        forall |i:int| 0 <= i < 5 ==> b[i] < a[i]
    ensures
        as_nat([
            (a[0] - b[0]) as u64,
            (a[1] - b[1]) as u64,
            (a[2] - b[2]) as u64,
            (a[3] - b[3]) as u64,
            (a[4] - b[4]) as u64
        ]) == as_nat(a) - as_nat(b)
{
    let c: [u64;5] = [
        (a[0] - b[0]) as u64,
        (a[1] - b[1]) as u64,
        (a[2] - b[2]) as u64,
        (a[3] - b[3]) as u64,
        (a[4] - b[4]) as u64
    ];
    // distribute pow2
    assert( as_nat(c) ==
        (a[0] - b[0]) +
        pow2(51) * a[1] - pow2(51) * b[1] +
        pow2(102) * a[2] - pow2(102) * b[2] +
        pow2(153) * a[3] - pow2(153) * b[3] +
        pow2(204) * a[4] - pow2(204) * b[4]
    ) by {
        lemma_mul_is_distributive_sub(pow2(1 * 51) as int, a[1] as int, b[1] as int);
        lemma_mul_is_distributive_sub(pow2(2 * 51) as int, a[2] as int, b[2] as int);
        lemma_mul_is_distributive_sub(pow2(3 * 51) as int, a[3] as int, b[3] as int);
        lemma_mul_is_distributive_sub(pow2(4 * 51) as int, a[4] as int, b[4] as int);
    }
}

pub fn negate(&mut self)
    requires
        forall|i: int| 0 <= i < 5 ==> old(self).limbs[i] < (1u64 << 51),
    ensures
        forall|i: int| 0 <= i < 5 ==> self.limbs[i] < (1u64 << 52),
        // Assume we start with l = (l0, l1, l2, l3, l4).
        // Using c0 = 2^51 - 19 and c = 2^51 - 1, we can see that
        // ( 36028797018963664u64 - l0,
        //   36028797018963952u64 - l1,
        //   36028797018963952u64 - l2,
        //   36028797018963952u64 - l3,
        //   36028797018963952u64 - l4 )
        // is just 16 * (c0, c, c, c, c) - l (in vector notation)
        // Further, as_nat((c0, c, c, c, c)) = p, so
        // as_nat(16 * (c0, c, c, c, c) - l) is 16p - as_nat(l)
        // We know as_nat(reduce(v)) = as_nat(v) - p * (v4 >> 51) for any v.
        // This gives us the identity
        // as_nat(negate(l)) = as_nat(reduce(16 * (c0, c, c, c, c) - l))
        //                   = 16p - as_nat(l) - p * ((16c - l4) >> 51)
        // Note that (16c - l4) >> 51 is either 14 or 15, in either case < 16.
        as_nat(self.limbs) == 16 * p() - as_nat(old(self).limbs) - p() * ((36028797018963952u64 - old(self).limbs[4]) as u64 >> 51),
        (as_nat(self.limbs) + as_nat(old(self).limbs)) % p() == 0
{
    proof {
        let c0 = (pow2(51) - 19);
        let c  = (pow2(51) - 1);
        lemma2_to64_rest(); // get pow2(51)
        // solver knows 36028797018963664u64 == 16 * c0
        // solver knows 36028797018963952u64 == 16 * c;

        assert forall |i: int| 0 <= i < 5 implies old(self).limbs[i] < 16 * c0 by {
            shift_is_pow2(51);
        }

        // Introduce 16p as a vector
        let v = [(16 * c0) as u64,(16 * c) as u64,(16 * c) as u64,(16 * c) as u64,(16 * c) as u64];

        assert(as_nat(v) == 16 * p()) by {
            // by definition of as_nat
            assert( as_nat(v) ==
                16 * c0 +
                pow2(51) * (16 * c) +
                pow2(102) * (16 * c) +
                pow2(153) * (16 * c) +
                pow2(204) * (16 * c)
            );

            // solver can reorder factors and pull out 16 on its own
            // ...

            // Write out `c`s and sum up powers
            assert( p() ==
                c0 +
                pow2(51) * c +
                pow2(102) * c +
                pow2(153) * c +
                pow2(204) * c
            ) by {
                lemma_pow2_adds(51, 51);
                lemma_pow2_adds(51, 102);
                lemma_pow2_adds(51, 153);
                lemma_pow2_adds(51, 204);
            }
        }

        let l0 = old(self).limbs[0];
        let l1 = old(self).limbs[1];
        let l2 = old(self).limbs[2];
        let l3 = old(self).limbs[3];
        let l4 = old(self).limbs[4];

        assert(as_nat([
            (16 * c0 - l0) as u64,
            (16 * c - l1) as u64,
            (16 * c - l2) as u64,
            (16 * c - l3) as u64,
            (16 * c - l4) as u64,
            ]) == as_nat(v) - as_nat(old(self).limbs)
        ) by {
            lemma_as_nat_sub(v, old(self).limbs);
        }

        let k = (16 * c - l4) as u64 >> 51;

        assert(
            16 * p() - as_nat(old(self).limbs) - p() * k + as_nat(old(self).limbs)
            ==
            p() * (16 - k)
        ) by {
            lemma_mul_is_distributive_sub(p() as int, 16, k as int)
        }

        assert((p() * (16 - k)) as nat % p() == 0) by {
            assert(k <= 16) by {
                assert(k <= (16 * pow2(51)) as u64 >> 51) by {
                    lemma_shr_le_u64((16 * c - l4) as u64, (16 * pow2(51)) as u64, 51);
                }
                // 16 * 2^51 / 2^51 = 16
                assert(((16 * 0x8000000000000) as u64 >> 51) == 16) by (compute);
            }
            lemma_mod_multiples_basic((16 - k) as int, p() as int);
        }
    }
    // See commentary in the Sub impl: (copied below)
        // To avoid underflow, first add a multiple of p.
        // Choose 16*p = p << 4 to be larger than 54-bit _rhs.
        //
        // If we could statically track the bitlengths of the limbs
        // of every FieldElement51, we could choose a multiple of p
        // just bigger than _rhs and avoid having to do a reduction.
        //
        // Since we don't yet have type-level integers to do this, we
        // have to add an explicit reduction call here.
    // Note on "magic numbers":
    // 36028797018963664u64 = 2^55 - 304 = 16 * (2^51 - 19)
    // 36028797018963952u64 = 2^55 - 16 =  16 * (2^51 - 1)
    let neg = FieldElement51::reduce([
        36028797018963664u64 - self.limbs[0],
        36028797018963952u64 - self.limbs[1],
        36028797018963952u64 - self.limbs[2],
        36028797018963952u64 - self.limbs[3],
        36028797018963952u64 - self.limbs[4],
    ]);
    self.limbs = neg.limbs;
}

pub(crate) fn as_affine_niels(&self) -> AffineNielsPoint {
    let recip = self.Z.invert();
    let x = &self.X * &recip;
    let y = &self.Y * &recip;
    let xy2d = &(&x * &y) * &constants::EDWARDS_D2;
    AffineNielsPoint {
        y_plus_x: &y + &x,
        y_minus_x: &y - &x,
        xy2d,
    }
}

pub(crate) fn as_projective_niels(&self) -> ProjectiveNielsPoint {
    ProjectiveNielsPoint {
        Y_plus_X: &self.Y + &self.X,
        Y_minus_X: &self.Y - &self.X,
        Z: self.Z,
        T2d: &self.T * &constants::EDWARDS_D2,
    }
}

fn multiscalar_mul<I, J>(scalars: I, points: J) -> EdwardsPoint
where
    I: IntoIterator,
    I::Item: Borrow<Scalar>,
    J: IntoIterator,
    J::Item: Borrow<EdwardsPoint>,
{
    use crate::backend::serial::curve_models::ProjectiveNielsPoint;
    use crate::traits::Identity;
    use crate::window::LookupTable;

    let lookup_tables: Vec<_> = points
        .into_iter()
        .map(|point| LookupTable::<ProjectiveNielsPoint>::from(point.borrow()))
        .collect();

    // This puts the scalar digits into a heap-allocated Vec.
    // To ensure that these are erased, pass ownership of the Vec into a
    // Zeroizing wrapper.
    #[cfg_attr(not(feature = "zeroize"), allow(unused_mut))]
    let mut scalar_digits: Vec<_> = scalars
        .into_iter()
        .map(|s| s.borrow().as_radix_16())
        .collect();

    let mut Q = EdwardsPoint::identity();
    for j in (0..64).rev() {
        Q = Q.mul_by_pow_2(4);
        let it = scalar_digits.iter().zip(lookup_tables.iter());
        for (s_i, lookup_table_i) in it {
            // R_i = s_{i,j} * P_i
            let R_i = lookup_table_i.select(s_i[j]);
            // Q = Q + R_i
            Q = (&Q + &R_i).as_extended();
        }
    }

    // #[cfg(feature = "zeroize")]
    // zeroize::Zeroize::zeroize(&mut scalar_digits);

    Q
}

pub(crate) proof fn lemma_sub_loop2_invariant(difference: Scalar52, i: usize, a: &Scalar52, b: &Scalar52, mask: u64, difference_after_loop1: Scalar52, difference_loop2_start: Scalar52, carry: u64, old_carry: u64, addend: u64, borrow: u64)
    requires
        0 <= i < 5,
        mask == (1u64 << 52) - 1,
        forall|j: int| 0 <= j < 5 ==> difference_loop2_start.limbs[j] < (1u64 << 52),
        forall|j: int| i <= j < 5 ==> difference_loop2_start.limbs[j] == difference_after_loop1.limbs[j],
        forall|j: int| (0 <= j < 5 && j!=i) ==> difference_loop2_start.limbs[j] == difference.limbs[j],
        mask == (1u64 << 52) - 1,
        i == 0 ==> old_carry == 0,
        i >= 1 ==> (old_carry >> 52) < 2,
        (i >=1 && borrow >> 63 == 0) ==> old_carry == difference_loop2_start.limbs[i-1],
        borrow >> 63 == 0 ==> difference_after_loop1 == difference_loop2_start,
        borrow >> 63 == 1 ==>
            seq_u64_to_nat(difference_after_loop1.limbs@.subrange(0, i as int)) + seq_u64_to_nat(constants::L.limbs@.subrange(0, i as int)) ==
            seq_u64_to_nat(difference_loop2_start.limbs@.subrange(0, i as int)) + (old_carry >> 52) * pow2(52 * i as nat),
        difference.limbs[i as int] == carry & mask,
        difference_loop2_start.limbs@.subrange(0, i as int) == difference.limbs@.subrange(0, i as int),
        borrow >> 63 == 0 ==> addend == 0,
        borrow >> 63 == 1 ==> addend == constants::L.limbs[i as int],
        carry == (old_carry >> 52) + difference_loop2_start.limbs[i as int] + addend,
    ensures
        (i+1 >=1 && borrow >> 63 == 0) ==> carry == difference.limbs[i as int],
        borrow >> 63 == 0 ==> difference_after_loop1 == difference,
        borrow >> 63 == 1 ==>
            seq_u64_to_nat(difference_after_loop1.limbs@.subrange(0, i+1 as int)) + seq_u64_to_nat(constants::L.limbs@.subrange(0, i+1 as int)) ==
            seq_u64_to_nat(difference.limbs@.subrange(0, i+1 as int)) + (carry >> 52) * pow2(52 * (i+1) as nat)
{
    if borrow >> 63 == 0 {
        lemma_old_carry(old_carry);
        assert(addend == 0);
        assert(carry == difference_loop2_start.limbs[i as int]);
        assert( carry & mask == carry ) by (bit_vector)
            requires
            carry < 1u64 <<52,
            mask == (1u64 << 52) - 1;
        assert(difference_after_loop1.limbs[i as int] == difference.limbs[i as int]);
        assert(forall |j :int| 0<=j<5 ==> difference_after_loop1.limbs[j] == difference.limbs[j]);
        assert(difference_after_loop1.limbs == difference.limbs);
    }
    if borrow >> 63 == 1 {
        // When underflow, addend = L.limbs[i]
        assert(addend == constants::L.limbs[i as int]);
        // carry = (old_carry >> 52) + difference_after_loop1.limbs[i] + L.limbs[i]
        // difference.limbs[i] = carry & mask
        calc! {
            (==)
            seq_u64_to_nat(difference_after_loop1.limbs@.subrange(0, i+1)) + seq_u64_to_nat(constants::L.limbs@.subrange(0, i+1)); {
                lemma_seq_u64_to_nat_subrange_extend(difference_after_loop1.limbs@, i as int);
                lemma_seq_u64_to_nat_subrange_extend(constants::L.limbs@, i as int);
            }
            seq_u64_to_nat(difference_after_loop1.limbs@.subrange(0, i as int)) + difference_after_loop1.limbs[i as int] as nat * pow2(52 * i as nat) +
            seq_u64_to_nat(constants::L.limbs@.subrange(0, i as int)) + constants::L.limbs[i as int] as nat * pow2(52 * i as nat); {
                broadcast use lemma_mul_is_distributive_add_other_way;
            }
            seq_u64_to_nat(difference_after_loop1.limbs@.subrange(0, i as int)) + seq_u64_to_nat(constants::L.limbs@.subrange(0, i as int)) +
            (difference_after_loop1.limbs[i as int] as nat + constants::L.limbs[i as int] as nat) * pow2(52 * i as nat); {
                // Use invariant
            }
            seq_u64_to_nat(difference_loop2_start.limbs@.subrange(0, i as int)) + (old_carry >> 52) as nat * pow2(52 * i as nat) +
            (difference_after_loop1.limbs[i as int] as nat + constants::L.limbs[i as int] as nat) * pow2(52 * i as nat); {
                assert(difference_loop2_start.limbs@.subrange(0, i as int) == difference.limbs@.subrange(0, i as int) );
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + (old_carry >> 52) as nat * pow2(52 * i as nat) +
            (difference_after_loop1.limbs[i as int] as nat + constants::L.limbs[i as int] as nat) * pow2(52 * i as nat); {
                broadcast use lemma_mul_is_distributive_add_other_way;
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + ((old_carry >> 52) as nat +
            difference_after_loop1.limbs[i as int] as nat + constants::L.limbs[i as int] as nat) * pow2(52 * i as nat); {
                assert(carry == (old_carry >> 52) + difference_after_loop1.limbs[i as int] + constants::L.limbs[i as int]);
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + carry as nat * pow2(52 * i as nat); {
                assert(carry == (carry >> 52) * (1u64<<52) + (carry & mask)) by (bit_vector)
                    requires mask == (1u64 << 52) - 1;
                assert(carry == (carry >> 52) * pow2(52) + difference.limbs[i as int]) by {
                    lemma2_to64_rest();
                    assert(0x10000000000000 == 1u64 << 52) by (compute_only);};
                    assert(difference.limbs[i as int] == carry & mask);
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + ((carry >> 52) as nat * pow2(52) + difference.limbs[i as int] as nat) * pow2(52 * i as nat); {
                broadcast use lemma_mul_is_distributive_add_other_way;
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + (carry >> 52) as nat * pow2(52) * pow2(52 * i as nat) + difference.limbs[i as int] as nat * pow2(52 * i as nat); {
                assert(pow2(52) * pow2(52 * i as nat) == pow2(52 + 52 * i as nat)) by {broadcast use lemma_pow2_adds;};
                assert(52 + 52 * i as nat == 52 * (i+1) as nat);
                assert((carry >> 52) as nat * pow2(52) * pow2(52 * i as nat) == (carry >> 52) as nat * pow2(52 * (i+1) as nat)) by {
                        assert(pow2(52) * pow2(52 * i as nat) == pow2(52 * (i+1) as nat));
                        lemma_mul_is_associative((carry >> 52) as int, pow2(52) as int, pow2(52 * i as nat) as int);
                };
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i as int)) + (carry >> 52) as nat * pow2(52 * (i+1) as nat) + difference.limbs[i as int] as nat * pow2(52 * i as nat); {
                lemma_seq_u64_to_nat_subrange_extend(difference.limbs@, i as int);
            }
            seq_u64_to_nat(difference.limbs@.subrange(0, i+1)) + (carry >> 52) as nat * pow2(52 * (i+1) as nat);
        }
    }
}

pub proof fn lemma_reorder_mul(a: int, b: int)
    ensures
        2 * (a * (19 * b)) == 19 * (2 * (a * b))
{
    // 2*( a * (19 * b)) = (2 * a) * (19 * b)
    lemma_mul_is_associative(2, a, 19 * b);
    // (2 * a) * (19 * b) = (19 * b) * (2 * a) = 19 * (b * (2 * a))
    lemma_mul_is_associative(19, b, 2 * a);
    // (b * (2 * a)) = (b * (a * 2)) = 2 * (a * b)
    lemma_mul_is_associative(b, a, 2);
}

