fn pow22501(&self) -> (FieldElement, FieldElement) {
    // Instead of managing which temporary variables are used
    // for what, we define as many as we need and leave stack
    // allocation to the compiler
    //
    // Each temporary variable t_i is of the form (self)^e_i.
    // Squaring t_i corresponds to multiplying e_i by 2,
    // so the pow2k function shifts e_i left by k places.
    // Multiplying t_i and t_j corresponds to adding e_i + e_j.
    //
    // Temporary t_i                      Nonzero bits of e_i
    //
    let t0  = self.square();           // 1         e_0 = 2^1
    let t1  = t0.square().square();    // 3         e_1 = 2^3
    let t2  = self * &t1;              // 3,0       e_2 = 2^3 + 2^0
    let t3  = &t0 * &t2;               // 3,1,0
    let t4  = t3.square();             // 4,2,1
    let t5  = &t2 * &t4;               // 4,3,2,1,0
    let t6  = t5.pow2k(5);             // 9,8,7,6,5
    let t7  = &t6 * &t5;               // 9,8,7,6,5,4,3,2,1,0
    let t8  = t7.pow2k(10);            // 19..10
    let t9  = &t8 * &t7;               // 19..0
    let t10 = t9.pow2k(20);            // 39..20
    let t11 = &t10 * &t9;              // 39..0
    let t12 = t11.pow2k(10);           // 49..10
    let t13 = &t12 * &t7;              // 49..0
    let t14 = t13.pow2k(50);           // 99..50
    let t15 = &t14 * &t13;             // 99..0
    let t16 = t15.pow2k(100);          // 199..100
    let t17 = &t16 * &t15;             // 199..0
    let t18 = t17.pow2k(50);           // 249..50
    let t19 = &t18 * &t13;             // 249..0

    (t19, t3)
}

pub(super) fn step_1(repr: &CompressedRistretto) -> (Choice, Choice, FieldElement) {
    // Step 1. Check s for validity:
    // 1.a) s must be 32 bytes (we get this from the type system)
    // 1.b) s < p
    // 1.c) s is nonnegative
    //
    // Our decoding routine ignores the high bit, so the only
    // possible failure for 1.b) is if someone encodes s in 0..18
    // as s+p in 2^255-19..2^255-1.  We can check this by
    // converting back to bytes, and checking that we get the
    // original input, since our encoding routine is canonical.

    let s = FieldElement::from_bytes(repr.as_bytes());
    let s_bytes_check = s.to_bytes();
    let s_encoding_is_canonical = s_bytes_check[..].ct_eq(repr.as_bytes());
    let s_is_negative = s.is_negative();

    (s_encoding_is_canonical, s_is_negative, s)
}

pub(crate) fn as_radix_2w(&self, w: usize) -> [i8; 64] {
    debug_assert!(w >= 4);
    debug_assert!(w <= 8);

    if w == 4 {
        return self.as_radix_16();
    }

    // Scalar formatted as four `u64`s with carry bit packed into the highest bit.
    let mut scalar64x4 = [0u64; 4];
    read_le_u64_into(&self.bytes, &mut scalar64x4[0..4]);

    let radix: u64 = 1 << w;
    let window_mask: u64 = radix - 1;

    let mut carry = 0u64;
    let mut digits = [0i8; 64];
    let digits_count = 256_usize.div_ceil(w);
    #[allow(clippy::needless_range_loop)]
    for i in 0..digits_count {
        // Construct a buffer of bits of the scalar, starting at `bit_offset`.
        let bit_offset = i * w;
        let u64_idx = bit_offset / 64;
        let bit_idx = bit_offset % 64;

        // Read the bits from the scalar
        let bit_buf: u64 = if bit_idx < 64 - w || u64_idx == 3 {
            // This window's bits are contained in a single u64,
            // or it's the last u64 anyway.
            scalar64x4[u64_idx] >> bit_idx
        } else {
            // Combine the current u64's bits with the bits from the next u64
            (scalar64x4[u64_idx] >> bit_idx) | (scalar64x4[1 + u64_idx] << (64 - bit_idx))
        };

        // Read the actual coefficient value from the window
        let coef = carry + (bit_buf & window_mask); // coef = [0, 2^r)

        // Recenter coefficients from [0,2^w) to [-2^w/2, 2^w/2)
        carry = (coef + (radix / 2)) >> w;
        digits[i] = ((coef as i64) - (carry << w) as i64) as i8;
    }

    // When 4 < w < 8, we can fold the final carry onto the last digit d,
    // because d < 2^w/2 so d + carry*2^w = d + 1*2^w < 2^(w+1) < 2^8.
    //
    // When w = 8, we can't fit carry*2^w into an i8.  This should
    // not happen anyways, because the final carry will be 0 for
    // reduced scalars, but Scalar invariant #1 allows 255-bit scalars.
    // To handle this, we expand the size_hint by 1 when w=8,
    // and accumulate the final carry onto another digit.
    match w {
        8 => digits[digits_count] += carry as i8,
        _ => digits[digits_count - 1] += (carry << w) as i8,
    }

    digits
}

pub const fn from_bytes(bytes: &[u8; 32]) -> FieldElement51 {
    const fn load8_at(input: &[u8], i: usize) -> u64 {
           (input[i] as u64)
        | ((input[i + 1] as u64) << 8)
        | ((input[i + 2] as u64) << 16)
        | ((input[i + 3] as u64) << 24)
        | ((input[i + 4] as u64) << 32)
        | ((input[i + 5] as u64) << 40)
        | ((input[i + 6] as u64) << 48)
        | ((input[i + 7] as u64) << 56)
    }

    let low_51_bit_mask = (1u64 << 51) - 1;
    FieldElement51(
    // load bits [  0, 64), no shift
    [  load8_at(bytes,  0)        & low_51_bit_mask
    // load bits [ 48,112), shift to [ 51,112)
    , (load8_at(bytes,  6) >>  3) & low_51_bit_mask
    // load bits [ 96,160), shift to [102,160)
    , (load8_at(bytes, 12) >>  6) & low_51_bit_mask
    // load bits [152,216), shift to [153,216)
    , (load8_at(bytes, 19) >>  1) & low_51_bit_mask
    // load bits [192,256), shift to [204,112)
    , (load8_at(bytes, 24) >> 12) & low_51_bit_mask
    ])
}

fn fmt(&self, f: &mut core::fmt::Formatter<'_>) -> core::fmt::Result {
    write!(
        f,
        "ProjectiveNielsPoint{{\n\tY_plus_X: {:?},\n\tY_minus_X: {:?},\n\tZ: {:?},\n\tT2d: {:?}\n}}",
        &self.Y_plus_X, &self.Y_minus_X, &self.Z, &self.T2d
    )
}

fn eq(&self, other: &Self) -> bool {
    self.ct_eq(other).into()
}

fn new<I>(static_points: I) -> Self
where
    I: IntoIterator,
    I::Item: Borrow<Self::Point>,
{
    Self {
        static_lookup_tables: static_points
            .into_iter()
            .map(|P| NafLookupTable8::<AffineNielsPoint>::from(P.borrow()))
            .collect(),
    }
}

fn is_empty(&self) -> bool {
    self.0.is_empty()
}

pub (crate) fn square_internal(a: &Scalar52) -> (z: [u128; 9])
requires
    limbs_bounded(a),
ensures
    slice128_to_nat(&z) == to_nat(&a.limbs) * to_nat(&a.limbs),
{
    proof {lemma_square_internal_no_overflow()}

    let mut z = [0u128; 9];
    z[0] = m(a.limbs[0], a.limbs[0]);
    z[1] = m(a.limbs[0], a.limbs[1]) * 2;
    z[2] = m(a.limbs[0], a.limbs[2]) * 2 + m(a.limbs[1], a.limbs[1]);
    z[3] = m(a.limbs[0], a.limbs[3]) * 2 + m(a.limbs[1], a.limbs[2]) * 2;
    z[4] = m(a.limbs[0], a.limbs[4]) * 2 + m(a.limbs[1], a.limbs[3]) * 2 + m(a.limbs[2], a.limbs[2]);
    z[5] =                 m(a.limbs[1], a.limbs[4]) * 2 + m(a.limbs[2], a.limbs[3]) * 2;
    z[6] =                                 m(a.limbs[2], a.limbs[4]) * 2 + m(a.limbs[3], a.limbs[3]);
    z[7] =                                                 m(a.limbs[3], a.limbs[4]) * 2;
    z[8] =                                                                 m(a.limbs[4], a.limbs[4]);

    proof {lemma_square_internal_correct(&a.limbs, &z);}

    z
}

pub open spec fn words_to_nat_gen_u64(words: &[u64], num_words: int, bits_per_word: int) -> nat
decreases num_words
{
    if num_words <= 0 {
        0
    } else {
        let word_value = (words[num_words - 1] as nat) * pow2(((num_words - 1) * bits_per_word) as nat);
        word_value + words_to_nat_gen_u64(words, num_words - 1, bits_per_word)
    }
}

fn reduce(mut limbs: [u64; 5]) -> (r: FieldElement51)
    ensures
        r.limbs == spec_reduce(limbs),
        forall|i: int| 0 <= i < 5 ==> r.limbs[i] < (1u64 << 52),
        (forall|i: int| 0 <= i < 5 ==> limbs[i] < (1u64 << 51)) ==> (r.limbs =~= limbs),
        as_nat(r.limbs) == as_nat(limbs) - p() * (limbs[4] >> 51),
        as_nat(r.limbs) % p() == as_nat(limbs) % p()
{
    proof {
        lemma_boundaries(limbs);
        lemma_reduce(limbs);
        pow255_gt_19();
        lemma_mod_multiples_vanish((limbs[4] >> 51) as int, as_nat(spec_reduce(limbs)) as int, p() as int);
    }

    // Since the input limbs are bounded by 2^64, the biggest
    // carry-out is bounded by 2^13.
    //
    // The biggest carry-in is c4 * 19, resulting in
    //
    // 2^51 + 19*2^13 < 2^51.0000000001
    //
    // Because we don't need to canonicalize, only to reduce the
    // limb sizes, it's OK to do a "weak reduction", where we
    // compute the carry-outs in parallel.

    let c0 = limbs[0] >> 51;
    let c1 = limbs[1] >> 51;
    let c2 = limbs[2] >> 51;
    let c3 = limbs[3] >> 51;
    let c4 = limbs[4] >> 51;

    limbs[0] &= LOW_51_BIT_MASK;
    limbs[1] &= LOW_51_BIT_MASK;
    limbs[2] &= LOW_51_BIT_MASK;
    limbs[3] &= LOW_51_BIT_MASK;
    limbs[4] &= LOW_51_BIT_MASK;

    limbs[0] += c4 * 19;
    limbs[1] += c0;
    limbs[2] += c1;
    limbs[3] += c2;
    limbs[4] += c3;

    // ADAPTED CODE LINE: limbs is now a named field
    FieldElement51{limbs}
}

fn conditional_select(a: &Self, b: &Self, choice: Choice) -> Self {
    Self(<[u8; 32]>::conditional_select(&a.0, &b.0, choice))
}

pub proof fn as_nat_squared(v: [u64; 5])
    ensures
        as_nat(v) * as_nat(v) ==
        pow2(8 * 51) * (v[4] * v[4]) +
        pow2(7 * 51) * (2 * (v[3] * v[4])) +
        pow2(6 * 51) * (v[3] * v[3] + 2 * (v[2] * v[4])) +
        pow2(5 * 51) * (2 * (v[2] * v[3]) + 2 * (v[1] * v[4])) +
        pow2(4 * 51) * (v[2] * v[2] + 2 * (v[1] * v[3]) + 2 * (v[0] * v[4])) +
        pow2(3 * 51) * (2 * (v[1] * v[2]) + 2 * (v[0] * v[3])) +
        pow2(2 * 51) * (v[1] * v[1] + 2 * (v[0] * v[2])) +
        pow2(1 * 51) * (2 * (v[0] * v[1])) +
                       (v[0] * v[0]),
        // and the mod equality
        (as_nat(v) * as_nat(v)) % p() ==
        (
            pow2(4 * 51) * (v[2] * v[2] + 2 * (v[1] * v[3]) + 2 * (v[0] * v[4])) +
            pow2(3 * 51) * (2 * (v[1] *  v[2]) + 2 * (v[0] *  v[3]) + 19 * (v[4] * v[4])) +
            pow2(2 * 51) * (v[1] * v[1] + 2 * (v[0] *  v[2]) + 19 * (2 * (v[3] * v[4]))) +
            pow2(1 * 51) * (2 * (v[0] *  v[1]) + 19 * (v[3] * v[3] + 2 * (v[2] * v[4]))) +
                           (v[0] *  v[0] + 19 * (2 * (v[2] * v[3]) + 2 * (v[1] * v[4])))
        ) as nat % p()
{
    let v0 = v[0];
    let v1 = v[1];
    let v2 = v[2];
    let v3 = v[3];
    let v4 = v[4];

    let s1 = pow2(1 * 51);
    let s2 = pow2(2 * 51);
    let s3 = pow2(3 * 51);
    let s4 = pow2(4 * 51);
    let s5 = pow2(5 * 51);
    let s6 = pow2(6 * 51);
    let s7 = pow2(7 * 51);
    let s8 = pow2(8 * 51);

    assert(s1 * s1 == s2) by {
        lemma_pow2_adds(51, 51)
    }
    assert(s1 * s2 == s2 * s1 == s3) by {
        lemma_pow2_adds(51, 102)
    }
    assert(s1 * s3 == s3 * s1 == s4) by {
        lemma_pow2_adds(51, 153)
    }
    assert(s1 * s4 == s4 * s1 == s5) by {
        lemma_pow2_adds(51, 204)
    }
    assert(s2 * s2 == s4) by {
        lemma_pow2_adds(102, 102)
    }
    assert(s2 * s3 == s3 * s2 == s5) by {
        lemma_pow2_adds(102, 153)
    }
    assert(s2 * s4 == s4 * s2 == s6) by {
        lemma_pow2_adds(102, 204)
    }
    assert(s3 * s3 == s6) by {
        lemma_pow2_adds(153, 153)
    }
    assert(s3 * s4 == s4 * s3 == s7) by {
        lemma_pow2_adds(153, 204)
    }
    assert(s4 * s4 == s8) by {
        lemma_pow2_adds(204, 204)
    }

    assert(as_nat(v) * as_nat(v) ==
        v0 * as_nat(v) +
        (s1 * v1) * as_nat(v) +
        (s2 * v2) * as_nat(v) +
        (s3 * v3) * as_nat(v) +
        (s4 * v4) * as_nat(v)
    ) by {
        // (x1 + x2 + x3 + x4 + x5) * n == x1 * n + x2 * n + x3 * n + x4 * n + x5 * n
        mul_5_terms_other_way(
            as_nat(v) as int,
            v0 as int,
            s1 * v1,
            s2 * v2,
            s3 * v3,
            s4 * v4
        );
    }

    // because of the sheer number of possible associativity/distributivity groupings we have
    // to help the solver along by intermittently asserting chunks
    assert(v0 * as_nat(v) ==
        s4 * (v0 * v4) +
        s3 * (v0 * v3) +
        s2 * (v0 * v2) +
        s1 * (v0 * v1) +
        v0 * v0
    ) by {
        mul_v0_and_reorder(
            v0 as int,
            s1 as int, v1 as int,
            s2 as int, v2 as int,
            s3 as int, v3 as int,
            s4 as int, v4 as int
        );
    }

    assert((s1 * v1) * as_nat(v) ==
        s5 * (v1 * v4) +
        s4 * (v1 * v3) +
        s3 * (v1 * v2) +
        s2 * (v1 * v1) +
        s1 * (v0 * v1)
    ) by {
        mul_si_vi_and_reorder(
            s1 as int, v1 as int,
            v0 as int,
            s1 as int, v1 as int,
            s2 as int, v2 as int,
            s3 as int, v3 as int,
            s4 as int, v4 as int
        )
    }

    assert((s2 * v2) * as_nat(v) ==
        s6 * (v2 * v4) +
        s5 * (v2 * v3) +
        s4 * (v2 * v2) +
        s3 * (v1 * v2) +
        s2 * (v0 * v2)
    ) by {
        mul_si_vi_and_reorder(
            s2 as int, v2 as int,
            v0 as int,
            s1 as int, v1 as int,
            s2 as int, v2 as int,
            s3 as int, v3 as int,
            s4 as int, v4 as int
        )
    }

    assert((s3 * v3) * as_nat(v) ==
        s7 * (v3 * v4) +
        s6 * (v3 * v3) +
        s5 * (v2 * v3) +
        s4 * (v1 * v3) +
        s3 * (v0 * v3)
    ) by {
        mul_si_vi_and_reorder(
            s3 as int, v3 as int,
            v0 as int,
            s1 as int, v1 as int,
            s2 as int, v2 as int,
            s3 as int, v3 as int,
            s4 as int, v4 as int
        )
    }

    assert((s4 * v4) * as_nat(v) ==
        s8 * (v4 * v4) +
        s7 * (v3 * v4) +
        s6 * (v2 * v4) +
        s5 * (v1 * v4) +
        s4 * (v0 * v4)
    ) by {
        mul_si_vi_and_reorder(
            s4 as int, v4 as int,
            v0 as int,
            s1 as int, v1 as int,
            s2 as int, v2 as int,
            s3 as int, v3 as int,
            s4 as int, v4 as int
        )
    }

    // we now mash them all together
    assert(as_nat(v) * as_nat(v) ==
        s8 * (v4 * v4) +
        s7 * (2 * (v3 * v4)) +
        s6 * (v3 * v3 + 2 * (v2 * v4)) +
        s5 * (2 * (v2 * v3) + 2 * (v1 * v4)) +
        s4 * (v2 * v2 + 2 * (v1 * v3) + 2 * (v0 * v4)) +
        s3 * (2 * (v1 * v2) + 2 * (v0 * v3)) +
        s2 * (v1 * v1 + 2 * (v0 * v2)) +
        s1 * (2 * (v0 * v1)) +
             (v0 * v0)
    ) by {
        // These assert(a + a = 2a) statements aren't strictly necessary, but they improve the solve time

        // s1 terms
        assert(
            s1 * (v0 * v1) + s1 * (v0 * v1)
            ==
            s1 * (2 * (v0 * v1))
        ) by {
            assert(v0 * v1 + v0 * v1 == 2 * (v0 * v1));
            lemma_mul_is_distributive_add(s1 as int, v0 * v1, v0 * v1);
        }

        // s2 terms
        assert(
            s2 * (v0 * v2) + s2 * (v1 * v1) + s2 * (v0 * v2)
            ==
            s2 * (v1 * v1 + 2 * (v0 * v2))
        ) by {
            assert(v0 * v2 + v0 * v2 == 2 * (v0 * v2));
            lemma_mul_is_distributive_add(s2 as int, v0 * v2, v0 * v2);
            lemma_mul_is_distributive_add(s2 as int, 2 * (v0 * v2), v1 * v1);
        }

        // s3 terms
        assert(
            s3 * (v0 * v3) + s3 * (v1 * v2) + s3 * (v1 * v2) + s3 * (v0 * v3)
            ==
            s3 * (2 * (v1 * v2) + 2 * (v0 * v3))
        ) by {
            assert(v1 * v2 + v1 * v2 == 2 * (v1 * v2));
            assert(v0 * v3 + v0 * v3 == 2 * (v0 * v3));
            lemma_mul_is_distributive_add(s3 as int, v1 * v2, v1 * v2);
            lemma_mul_is_distributive_add(s3 as int, v0 * v3, v0 * v3);
            lemma_mul_is_distributive_add(s3 as int, 2 * (v1 * v2), 2 * (v0 * v3));
        }

        // s4 terms
        assert(
            s4 * (v0 * v4) + s4 * (v1 * v3) + s4 * (v2 * v2) + s4 * (v1 * v3) + s4 * (v0 * v4)
            ==
            s4 * (v2 * v2 + 2 * (v1 * v3) + 2 * (v0 * v4))
        ) by {
            assert(v0 * v4 + v0 * v4 == 2 * (v0 * v4));
            assert(v1 * v3 + v1 * v3 == 2 * (v1 * v3));
            lemma_mul_is_distributive_add(s4 as int, v0 * v4, v0 * v4);
            lemma_mul_is_distributive_add(s4 as int, v1 * v3, v1 * v3);
            lemma_mul_is_distributive_add(s4 as int, v2 * v2, 2 * (v1 * v3));
            lemma_mul_is_distributive_add(s4 as int, v2 * v2 + 2 * (v1 * v3), 2 * (v0 * v4));
        }

        // s5 terms
        assert(
            s5 * (v1 * v4) + s5 * (v2 * v3) + s5 * (v2 * v3) + s5 * (v1 * v4)
            ==
            s5 * (2 * (v2 * v3) + 2 * (v1 * v4))
        ) by {
            assert(v1 * v4 + v1 * v4 == 2 * (v1 * v4));
            assert(v2 * v3 + v2 * v3 == 2 * (v2 * v3));
            lemma_mul_is_distributive_add(s5 as int, v1 * v4, v1 * v4);
            lemma_mul_is_distributive_add(s5 as int, v2 * v3, v2 * v3);
            lemma_mul_is_distributive_add(s5 as int, 2 * (v1 * v4), 2 * (v2 * v3));
        }

        // s6 terms
        assert(
            s6 * (v2 * v4) + s6 * (v3 * v3) + s6 * (v2 * v4)
            ==
            s6 * (v3 * v3 + 2 * (v2 * v4))
        ) by {
            assert(v2 * v4 + v2 * v4 == 2 * (v2 * v4));
            lemma_mul_is_distributive_add(s6 as int, v2 * v4, v2 * v4);
            lemma_mul_is_distributive_add(s6 as int, 2 * (v2 * v4), v3 * v3);
        }

        // s7 terms
        assert(
            s7 * (v3 * v4) + s7 * (v3 * v4)
            ==
            s7 * (2 * (v3 * v4))
        ) by {
            assert(v3 * v4 + v3 * v4 == 2 * (v3 * v4));
            lemma_mul_is_distributive_add(s7 as int, v3 * v4, v3 * v4);
        }
    }

    // This is the explicit version, now we can take everything mod p

    // p well defined
    pow255_gt_19();

    // By definition, p = s^5 - 19
    // equivalently,
    // s^5 = (p + 19)
    // s^6 = s * (p + 19)
    // s^7 = s^2 * (p + 19)
    // s^8 = s^3 * (p + 19)
    assert(s5 == (p() + 19));

    // we pack together terms to slim down expressions;

    let c0_base = v0 *  v0;
    let c0_x19 = 2 * (v2 * v3) + 2 * (v1 * v4);
    let c0 = c0_base + 19 * c0_x19;

    let c1_base = 2 * (v0 *  v1);
    let c1_x19 = v3 * v3 + 2 * (v2 * v4);
    let c1 = c1_base + 19 * c1_x19;

    let c2_base = v1 * v1 + 2 * (v0 *  v2);
    let c2_x19 = 2 * (v3 * v4);
    let c2 = c2_base + 19 * c2_x19;

    let c3_base = 2 * (v1 *  v2) + 2 * (v0 *  v3);
    let c3_x19 = v4 * v4;
    let c3 = c3_base + 19 * c3_x19;

    let c4 = v2 *  v2 + 2 * (v1 *  v3) + 2 * (v0 *  v4);

    // group in preparation for the substitution
    assert(as_nat(v) * as_nat(v) ==
        s4 * c4 +
        s3 * (s5 * c3_x19 + c3_base) +
        s2 * (s5 * c2_x19 + c2_base) +
        s1 * (s5 * c1_x19 + c1_base) +
             (s5 * c0_x19 + c0_base)
    ) by {
        // s3 terms
        assert(
            s8 * c3_x19 + s3 * c3_base
            ==
            s3 * (s5 * c3_x19 + c3_base)
        ) by {
            assert(s8 == (s3 * s5)) by {
                lemma_pow2_adds(3 * 51, 5 * 51);
            }
            lemma_mul_is_associative(s3 as int, s5 as int, c3_x19);
            lemma_mul_is_distributive_add(s3 as int, s5 * c3_x19, c3_base)
        }

        // s2 terms
        assert(
            s7 * c2_x19 + s2 * c2_base
            ==
            s2 * (s5 * c2_x19 + c2_base)
        ) by {
            assert(s7 == (s2 * s5)) by {
                lemma_pow2_adds(2 * 51, 5 * 51);
            }
            lemma_mul_is_associative(s2 as int, s5 as int, c2_x19);
            lemma_mul_is_distributive_add(s2 as int, s5 * c2_x19, c2_base)
        }

        // s1 terms
        assert(
            s6 * c1_x19 + s1 * c1_base
            ==
            s1 * (s5 * c1_x19 + c1_base)
        ) by {
            assert(s6 == (s1 * s5)) by {
                lemma_pow2_adds(1 * 51, 5 * 51);
            }
            lemma_mul_is_associative(s1 as int, s5 as int, c1_x19);
            lemma_mul_is_distributive_add(s1 as int, s5 * c1_x19, c1_base)
        }
    }

    // Next we use the identity s5 = p + 19
    assert(s5 * c3_x19 + c3_base == p() * c3_x19 + c3) by {
        lemma_mul_is_distributive_add(c3_x19 as int, p() as int, 19);
    }

    assert(s5 * c2_x19 + c2_base == p() * c2_x19 + c2) by {
        lemma_mul_is_distributive_add(c2_x19 as int, p() as int, 19);
    }

    assert(s5 * c1_x19 + c1_base == p() * c1_x19 + c1) by {
        lemma_mul_is_distributive_add(c1_x19 as int, p() as int, 19);
    }

    assert(s5 * c0_x19 + c0_base == p() * c0_x19 + c0) by {
        lemma_mul_is_distributive_add(c0_x19 as int, p() as int, 19);
    }

    // in summary, we can reorder and regroup terms to get X * p() + Y
    assert(as_nat(v) * as_nat(v) ==
        p() * ( s3 * c3_x19 + s2 * c2_x19 + s1 * c1_x19 + c0_x19 ) +
        (
            s4 * c4 +
            s3 * c3 +
            s2 * c2 +
            s1 * c1 +
                 c0
        )
    ) by {
        lemma_mul_is_distributive_add(s3 as int, p() * c3_x19, c3 as int);
        lemma_mul_is_distributive_add(s2 as int, p() * c2_x19, c2 as int);
        lemma_mul_is_distributive_add(s1 as int, p() * c1_x19, c1 as int);

        assert(
            s3 * (p() * c3_x19) + s2 * (p() * c2_x19) + s1 * (p() * c1_x19) + p() * c0_x19
            ==
            p() * ( s3 * c3_x19 + s2 * c2_x19 + s1 * c1_x19 + c0_x19 )
        ) by {
            lemma_mul_is_associative(s3 as int, c3_x19 as int, p() as int);
            lemma_mul_is_associative(s2 as int, c2_x19 as int, p() as int);
            lemma_mul_is_associative(s1 as int, c1_x19 as int, p() as int);

            lemma_mul_is_distributive_add(p() as int, s3 * c3_x19, s2 * c2_x19);
            lemma_mul_is_distributive_add(p() as int, s3 * c3_x19 + s2 * c2_x19, s1 * c1_x19);
            lemma_mul_is_distributive_add(p() as int, s3 * c3_x19 + s2 * c2_x19 + s1 * c1_x19, c0_x19 as int);
        }
    }


    let k = ( s3 * c3_x19 + s2 * c2_x19 + s1 * c1_x19 + c0_x19 );
    let sum = (
            s4 * c4 +
            s3 * c3 +
            s2 * c2 +
            s1 * c1 +
                 c0
        );

    assert(as_nat(v) * as_nat(v) == k * p() + sum);
    assert(k * p() + sum == (k as nat) * p() + (sum as nat));

    // Now, we simply move to mod p

    assert((as_nat(v) * as_nat(v)) % p() == ((k as nat) * p() + (sum as nat)) % p() );
    assert(
        ((k as nat) * p() + (sum as nat)) % p() ==
        (sum as nat) % p()
    ) by {
        lemma_mod_sum_factor(k as int, sum as int, p() as int);
    }

    // sanity check
    assert(s4 * c4 == pow2(4 * 51) * (v[2] * v[2] + 2 * (v[1] * v[3]) + 2 * (v[0] * v[4])));
    assert(s3 * c3 == pow2(3 * 51) * (2 * (v[1] *  v[2]) + 2 * (v[0] *  v[3]) + 19 * (v[4] * v[4])));
    assert(s2 * c2 == pow2(2 * 51) * (v[1] * v[1] + 2 * (v[0] *  v[2]) + 19 * (2 * (v[3] * v[4]))));
    assert(s1 * c1 == pow2(1 * 51) * (2 * (v[0] *  v[1]) + 19 * (v[3] * v[3] + 2 * (v[2] * v[4]))));
    assert(c0 == (v[0] *  v[0] + 19 * (2 * (v[2] * v[3]) + 2 * (v[1] * v[4]))));
}

