    RistrettoPoint(EdwardsPoint::conditional_select(&a.0, &b.0, choice))
}

pub fn to_bytes(self) -> [u8; 32] {
    // Let h = limbs[0] + limbs[1]*2^51 + ... + limbs[4]*2^204.
    //
    // Write h = pq + r with 0 <= r < p.
    //
    // We want to compute r = h mod p.
    //
    // If h < 2*p = 2^256 - 38,
    // then q = 0 or 1,
    //
    // with q = 0 when h < p
    //  and q = 1 when h >= p.
    //
    // Notice that h >= p <==> h + 19 >= p + 19 <==> h + 19 >= 2^255.
    // Therefore q can be computed as the carry bit of h + 19.

    // First, reduce the limbs to ensure h < 2*p.
    let mut limbs = FieldElement51::reduce(self.0).0;

    let mut q = (limbs[0] + 19) >> 51;
    q = (limbs[1] + q) >> 51;
    q = (limbs[2] + q) >> 51;
    q = (limbs[3] + q) >> 51;
    q = (limbs[4] + q) >> 51;

    // Now we can compute r as r = h - pq = r - (2^255-19)q = r + 19q - 2^255q

    limbs[0] += 19 * q;

    // Now carry the result to compute r + 19q ...
    let low_51_bit_mask = (1u64 << 51) - 1;
    limbs[1] += limbs[0] >> 51;
    limbs[0] &= low_51_bit_mask;
    limbs[2] += limbs[1] >> 51;
    limbs[1] &= low_51_bit_mask;
    limbs[3] += limbs[2] >> 51;
    limbs[2] &= low_51_bit_mask;
    limbs[4] += limbs[3] >> 51;
    limbs[3] &= low_51_bit_mask;
    // ... but instead of carrying (limbs[4] >> 51) = 2^255q
    // into another limb, discard it, subtracting the value
    limbs[4] &= low_51_bit_mask;

    // Now arrange the bits of the limbs.
    let mut s = [0u8;32];
    s[ 0] =   limbs[0]                           as u8;
    s[ 1] =  (limbs[0] >>  8)                    as u8;
    s[ 2] =  (limbs[0] >> 16)                    as u8;
    s[ 3] =  (limbs[0] >> 24)                    as u8;
    s[ 4] =  (limbs[0] >> 32)                    as u8;
    s[ 5] =  (limbs[0] >> 40)                    as u8;
    s[ 6] = ((limbs[0] >> 48) | (limbs[1] << 3)) as u8;
    s[ 7] =  (limbs[1] >>  5)                    as u8;
    s[ 8] =  (limbs[1] >> 13)                    as u8;
    s[ 9] =  (limbs[1] >> 21)                    as u8;
    s[10] =  (limbs[1] >> 29)                    as u8;
    s[11] =  (limbs[1] >> 37)                    as u8;
    s[12] = ((limbs[1] >> 45) | (limbs[2] << 6)) as u8;
    s[13] =  (limbs[2] >>  2)                    as u8;
    s[14] =  (limbs[2] >> 10)                    as u8;
    s[15] =  (limbs[2] >> 18)                    as u8;
    s[16] =  (limbs[2] >> 26)                    as u8;
    s[17] =  (limbs[2] >> 34)                    as u8;
    s[18] =  (limbs[2] >> 42)                    as u8;
    s[19] = ((limbs[2] >> 50) | (limbs[3] << 1)) as u8;
    s[20] =  (limbs[3] >>  7)                    as u8;
    s[21] =  (limbs[3] >> 15)                    as u8;
    s[22] =  (limbs[3] >> 23)                    as u8;
    s[23] =  (limbs[3] >> 31)                    as u8;
    s[24] =  (limbs[3] >> 39)                    as u8;
    s[25] = ((limbs[3] >> 47) | (limbs[4] << 4)) as u8;
    s[26] =  (limbs[4] >>  4)                    as u8;
    s[27] =  (limbs[4] >> 12)                    as u8;
    s[28] =  (limbs[4] >> 20)                    as u8;
    s[29] =  (limbs[4] >> 28)                    as u8;
    s[30] =  (limbs[4] >> 36)                    as u8;
    s[31] =  (limbs[4] >> 44)                    as u8;

    // High bit should be zero.
    debug_assert!((s[31] & 0b1000_0000u8) == 0u8);

    s
}

pub proof fn mul_5_terms(n: int, x1: int, x2: int, x3: int, x4: int, x5: int)
    ensures
        n * (x1 + x2 + x3 + x4 + x5) == n * x1 + n * x2 + n * x3 + n * x4 + n * x5
{
    // N * ((((x0 + x1) + x2) + x3) + x4) = N * (((x0 + x1) + x2) + x3) + N * x4
    lemma_mul_is_distributive_add(n, x1 + x2 + x3 + x4, x5);
    // N * (((x0 + x1) + x2) + x3) = N * ((x0 + x1) + x2) + N * x3
    lemma_mul_is_distributive_add(n, x1 + x2 + x3, x4);
    // N * ((x0 + x1) + x2) = N * (x0 + x1) + N * x2
    lemma_mul_is_distributive_add(n, x1 + x2, x3);
    // N * (x0 + x1) = N * x0 + N * x1
    lemma_mul_is_distributive_add(n, x1, x2);
}

pub const fn from_bytes(bytes: &[u8; 32]) -> (r: FieldElement51)
    ensures
        true
        // TODO:
        // as_nat(r.limbs) =?= as_nat_32_u8(bytes)
{
    proof {
        l51_bit_mask_lt() // No over/underflow in the below let-def
    }
    let low_51_bit_mask = (1u64 << 51) - 1;
    // ADAPTED CODE LINE: limbs is now a named field
    FieldElement51{ limbs:
    // load bits [  0, 64), no shift
    [  load8_at(bytes,  0)        & low_51_bit_mask
    // load bits [ 48,112), shift to [ 51,112)
    , (load8_at(bytes,  6) >>  3) & low_51_bit_mask
    // load bits [ 96,160), shift to [102,160)
    , (load8_at(bytes, 12) >>  6) & low_51_bit_mask
    // load bits [152,216), shift to [153,216)
    , (load8_at(bytes, 19) >>  1) & low_51_bit_mask
    // load bits [192,256), shift to [204,112)
    , (load8_at(bytes, 24) >> 12) & low_51_bit_mask
    ]}
}

